import {Project} from '../../models/project';
import {Element} from '../../models/element';
import * as PIXI from 'pixi.js';
import {getStaticDI} from '../../models/get-di';
import {ThemingService} from '../theming/theming.service';
import {environment} from '../../../environments/environment';
import {DefaultRenderer} from './svg-renderer/default-renderer';
import {ElementTypeId} from '../../models/element-types/element-type-ids';
import {LedRenderer} from './svg-renderer/led-renderer';
import {TextRenderer} from './svg-renderer/text-renderer';
import {ButtonRenderer} from './svg-renderer/button-renderer';
import {LeverRenderer} from './svg-renderer/lever-renderer';
import {InputOutputRenderer} from './svg-renderer/input-output-renderer';
import {LedMatrixRenderer} from './svg-renderer/led-matrix-renderer';
import {SegmentDisplayRenderer} from './svg-renderer/segment-display-renderer';

export class SvgImageExporter {

	private readonly SVG_NS = 'http://www.w3.org/2000/svg';
	private readonly themingService = getStaticDI(ThemingService);
	private readonly _svg: SVGElement;

	public readonly size = new PIXI.Point(0, 0);
	public readonly offset = new PIXI.Point(0, 0);
	public readonly scaleFactor;
	public readonly gridSize: number;
	public readonly quality: RenderQuality = RenderQuality.full;
	public theme: Theme;

	constructor(private project: Project, size?: PIXI.Point, theme?: Theme) {
		this._svg = document.createElementNS(this.SVG_NS, 'svg');
		this.theme = theme ?? (this.themingService.currentTheme === 'dark' ? Theme.Dark : Theme.Light);

		let minX = Number.MAX_SAFE_INTEGER;
		let minY = Number.MAX_SAFE_INTEGER;
		let maxX = 0;
		let maxY = 0;

		for (const action of this.project.getOpenActions()) {
			if (action.name !== 'addComp' && action.name !== 'addWire')
				continue;

			if (action.pos.x < minX)
				minX = action.pos.x;
			if (action.pos.y < minY)
				minY = action.pos.y;
			if (action.endPos.x > maxX)
				maxX = action.endPos.x;
			if (action.endPos.y > maxY)
				maxY = action.endPos.y;
		}

		minX--;
		minY--;
		maxX++;
		maxY++;

		this.scaleFactor = 1;
		if (size) {
			this.size = size;
			this.gridSize = size.x / (maxX - minX) < size.y / (maxY - minY) ? size.x / (maxX - minX) : size.y / (maxY - minY);
			if (this.gridSize < 2) {
				this.gridSize = 2;
				this.scaleFactor = size.x / (maxX - minX) < size.y / (maxY - minY) ? size.x / (maxX - minX) / 2 : size.y / (maxY - minY) / 2;
				this.size.x /= this.scaleFactor;
				this.size.y /= this.scaleFactor;
			}
			if (this.gridSize <= 2)
				this.quality = RenderQuality.low;
			else if (this.gridSize <= 4)
				this.quality = RenderQuality.high;
		} else {
			this.gridSize = environment.gridPixelWidth;
			this.size.x = (maxX - minX) * this.gridSize;
			this.size.y = (maxY - minY) * this.gridSize;
		}

		this.offset.x = Math.trunc(-minX * this.gridSize + 1 + (this.size.x - (maxX - minX) * this.gridSize) / 2) + 0.5;
		this.offset.y = Math.trunc(-minY * this.gridSize + 1 + (this.size.y - (maxY - minY) * this.gridSize) / 2) + 0.5;

		this._svg.setAttribute('height', this.size.y + '');
		this._svg.setAttribute('width', this.size.x + '');

		// console.log(minX);
		// console.log(minY);
		// console.log(maxX);
		// console.log(maxY);
		// console.log(this.offset);
		// console.log('GridSize', this.gridSize);
		// console.log('Size', this.size);
		// console.log('ScaleFactor', this.scaleFactor);
		// console.log('Quality', this.quality);

		this.generateDefinitions();
		/*
		if (this.quality >= RenderQuality.full) {
			const bg = document.createElementNS(this.SVG_NS, 'rect');
			bg.setAttribute('x', '0');
			bg.setAttribute('y', '0');
			bg.setAttribute('width', '100%');
			bg.setAttribute('height', '100%');
			bg.setAttribute('fill', 'url(#bg)');
			this._svg.appendChild(bg);
		}
		*/

		let wirePath = '';
		const wires = document.createElementNS(this.SVG_NS, 'path');
		wires.setAttribute('class', 'w');
		this._svg.appendChild(wires);

		for (const action of this.project.getOpenActions()) {
			switch (action.name) {
				case 'addComp':
					this.placeComp(action.element);
					break;
				case 'addWire':
					if (action.element.pos.y === action.element.endPos.y)
						wirePath += ` M ${(action.element.pos.x + 0.5) * this.gridSize + this.offset.x},${(action.element.pos.y + 0.5) * this.gridSize + this.offset.y} H ${(action.element.endPos.x + 0.5) * this.gridSize + this.offset.x}`;
					else
						wirePath += ` M ${(action.element.pos.x + 0.5) * this.gridSize + this.offset.x},${(action.element.pos.y + 0.5) * this.gridSize + this.offset.y} V ${(action.element.endPos.y + 0.5) * this.gridSize + this.offset.y}`;
					break;
				case 'conWire':
					if (this.quality >= RenderQuality.high)
						this.placeConnPoint(action.pos);
					break;
			}
		}
		wires.setAttribute('d', wirePath);
	}

	private static toStringTheme(theme: Theme) {
		if (theme % 2 === 0)
			return 'dark';
		else
			return 'light';
	}

	private placeComp(element: Element) {
		let group: SVGGElement;
		switch (element.typeId) {
			case ElementTypeId.TEXT:
				group = new TextRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			case ElementTypeId.LED:
				group = new LedRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			case ElementTypeId.LED_MATRIX:
				group = new LedMatrixRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			case ElementTypeId.BUTTON:
				group = new ButtonRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			case ElementTypeId.INPUT:
			case ElementTypeId.OUTPUT:
				group = new InputOutputRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			case ElementTypeId.LEVER:
				group = new LeverRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			case ElementTypeId.SEGMENT_DISPLAY:
				group = new SegmentDisplayRenderer(element, this.gridSize, this.quality, this.project).render();
				break;
			default:
				group = new DefaultRenderer(element, this.gridSize, this.quality, this.project).render();
		}
		if (group) {
			group.setAttribute('transform', `translate(${element.pos.x * this.gridSize + this.offset.x}, ${element.pos.y * this.gridSize + this.offset.y})`);
			this._svg.appendChild(group);
		}
	}

	private placeConnPoint(pos: PIXI.Point) {
		const point = document.createElementNS(this.SVG_NS, 'rect');
		point.setAttribute('x', (pos.x + 0.5) * this.gridSize + this.offset.x - (this.gridSize < 8 ? 1.5 : 2.5) + '');
		point.setAttribute('y', (pos.y + 0.5) * this.gridSize + this.offset.y - (this.gridSize < 8 ? 1.5 : 2.5) + '');
		point.setAttribute('width', this.gridSize < 8 ? '3' : '5');
		point.setAttribute('height', this.gridSize < 8 ? '3' : '5');
		point.setAttribute('class', 'cp');
		this._svg.appendChild(point);
	}

	private generateDefinitions() {
		const defs = document.createElementNS(this.SVG_NS, 'defs');
		const styles = document.createElementNS(this.SVG_NS, 'style');
		styles.setAttribute('type', 'text/css');
		styles.innerHTML = `
			@font-face {
                font-family: 'Roboto';
                font-style: normal;
                font-weight: 500;
                src: url(data:application/font-woff;charset=utf-8;base64,) format('woff');
			}
			@font-face {
				font-family: 'DSEG7';
				src: url(data:application/font-woff;charset=utf-8;base64,) format('woff');
			}
			@font-face {
				font-family: 'DSEG14';
				src: url(data:application/font-woff;charset=utf-8;base64,) format('woff');
			}
			text {
				fill: #${this.themingService.getEditorColor('fontTint', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
				font-family: Roboto, Arial, sans-serif;
			}
			.body {
				background-color: ${this.theme > Theme.Light ? 'transparent' : '#' + this.themingService.getEditorColor('background', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
			}
			.w {
				stroke: #${this.themingService.getEditorColor('wire', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
				stroke-width: 1px;
				fill: none;
				vector-effect: non-scaling-stroke;
			}
			.c {
				stroke: #${this.themingService.getEditorColor('wire', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
				stroke-width: 1px;
				fill: ${this.theme > Theme.Light ? 'none' : '#' + this.themingService.getEditorColor('background', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
				vector-effect: non-scaling-stroke;
			}
			.cp {
				fill: #${this.themingService.getEditorColor('wire', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
			}
			.s {
				text-anchor: middle;
				font-size: ${this.gridSize * 0.6}px;
			}
			.pi {
				text-anchor: middle;
				font-size: ${6 * this.gridSize / environment.gridPixelWidth}px;
			}
			.text {
				text-anchor: start;
				text-align: center;
			}
			.led {
				fill: #${this.themingService.getEditorColor('ledOff', SvgImageExporter.toStringTheme(this.theme)).toString(16).padStart(6, '0')};
			}
			.seg7 {
				text-anchor: middle;
				font-size: ${this.gridSize * 1.35}px;
				font-family: DSEG7, DSEG14, Roboto, Arial, sans-serif;
			}
			.seg14 {
				text-anchor: middle;
				font-size: ${this.gridSize * 1.35}px;
				font-family: DSEG14, Roboto, Arial, sans-serif;
			}
			.sb {
				text-anchor: start;
				font-size: ${this.gridSize * 0.4}px;
				font-family: DSEG7, DSEG14, Roboto, Arial, sans-serif;
			}
			.l-l {
				text-anchor: start;
				font-size: ${this.gridSize * 0.4}px;
			}
			.l-r {
				text-anchor: end;
				font-size: ${this.gridSize * 0.4}px;
			}
			.l-t {
				text-anchor: middle;
				font-size: ${this.gridSize * 0.4}px;
			}
			.l-b {
				text-anchor: middle;
				font-size: ${this.gridSize * 0.4}px;
			}
		`;

		const pattern = document.createElementNS(this.SVG_NS, 'pattern');
		pattern.setAttribute('id', 'bg');
		pattern.setAttribute('x', '1');
		pattern.setAttribute('y', '1');
		pattern.setAttribute('width', this.gridSize + '');
		pattern.setAttribute('height', this.gridSize + '');
		pattern.setAttribute('patternUnits', 'userSpaceOnUse');

		const patternRect = document.createElementNS(this.SVG_NS, 'rect');
		patternRect.setAttribute('x', '0');
		patternRect.setAttribute('y', '0');
		patternRect.setAttribute('width', '1');
		patternRect.setAttribute('height', '1');
		patternRect.setAttribute('class', 'w');
		pattern.appendChild(patternRect);

		defs.appendChild(styles);
		defs.appendChild(pattern);
		this._svg.appendChild(defs);
		this._svg.setAttribute('class', 'body');
	}

	public serializeSVG(): string {
		const serializer = new XMLSerializer();
		let source = serializer.serializeToString(this._svg);
		if (!source.match(/^<svg[^>]+xmlns="http\:\/\/www\.w3\.org\/2000\/svg"/)) {
			source = source.replace(/^<svg/, '<svg xmlns="http://www.w3.org/2000/svg"');
		}
		if (!source.match(/^<svg[^>]+"http\:\/\/www\.w3\.org\/1999\/xlink"/)) {
			source = source.replace(/^<svg/, '<svg xmlns:xlink="http://www.w3.org/1999/xlink"');
		}
		return '<?xml version="1.0" standalone="no"?>\r\n' + source;
	}

	public changeTheme(theme: Theme) {
		this.theme = theme;
		this._svg.removeChild(this._svg.getElementsByTagNameNS(this.SVG_NS, 'defs')[0]);
		this.generateDefinitions();
	}

	public getBase64String(): string {
		return 'data:image/svg+xml;base64,' + btoa(unescape(encodeURIComponent(this.serializeSVG())));
	}
}

export const enum RenderQuality {
	low,
	high,
	full
}

export const enum Theme {
	Dark,
	Light,
	Dark_Transparent,
	Light_Transparent
}
